CREATE TABLE `24t`.`student` ( `stud_id` INT(100) NOT NULL AUTO_INCREMENT , `stud_fname` VARCHAR(50) NOT NULL , `stud_lname` VARCHAR(50) NOT NULL , `stud_email` VARCHAR(100) NOT NULL , `stud_usrn` VARCHAR(20) NOT NULL , `stud_pass` VARCHAR(50) NOT NULL , `stud_hash` VARCHAR(32) NOT NULL , `stud_active` TINYINT(1) NOT NULL , `stud_travel` INT(2) NOT NULL , `stud_zip` INT(5) NOT NULL , `stud_addr` VARCHAR(100) NOT NULL , `stud_profilepic` VARCHAR(20) NOT NULL , `stud_gender` ENUM('M','F') NOT NULL , `stud_intro` TEXT NOT NULL , `stud_birthdate` DATE NOT NULL , `stud_registerdate` TIMESTAMP NOT NULL , `stud_lastin` TIMESTAMP NOT NULL , PRIMARY KEY (`stud_id`)) ENGINE = InnoDB;
CREATE TABLE `24t`.`tutor` ( `tutor_id` INT(100) NOT NULL AUTO_INCREMENT , `t_fname` VARCHAR(50) NOT NULL , `t_lname` VARCHAR(50) NOT NULL , `t_email` VARCHAR(100) NOT NULL , `t_usrn` VARCHAR(20) NOT NULL , `t_pass` VARCHAR(50) NOT NULL , `t_hash` INT(32) NOT NULL , `t_active` TINYINT(1) NOT NULL , `t_travel` INT(2) NOT NULL , `t_zip` INT(5) NOT NULL , `t_addr` VARCHAR(100) NOT NULL , `t_profilepic` VARCHAR(20) NOT NULL , `t_gender` ENUM('M','F') NOT NULL , `t_intro` TEXT NOT NULL , `t_job` VARCHAR(50) NOT NULL , `t_birthdate` DATE NOT NULL , `t_qualifications` VARCHAR(50) NOT NULL , `t_officialcheck` TINYINT(1) NOT NULL , `t_registerdate` TIMESTAMP NOT NULL , `t_lastin` TIMESTAMP NOT NULL , PRIMARY KEY (`tutor_id`)) ENGINE = InnoDB;
CREATE TABLE `24t`.`payment_info` ( `payment_id` INT(100) NOT NULL AUTO_INCREMENT , `paytime` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `payamount` INT(30) NOT NULL , `stud_id` INT(100) NOT NULL , `tutor_id` INT(100) NOT NULL , `bookingdate` DATETIME NOT NULL , `appoint_hrs` INT NOT NULL , `status` ENUM('pending','cancel','success') NOT NULL , `cancel_reason` VARCHAR(100) NULL , PRIMARY KEY (`payment_id`)) ENGINE = InnoDB;

